const t=JSON.parse('{"key":"v-1b932586","path":"/%E8%AF%97%E8%97%8F/%E8%AF%8D%E8%AF%9D/%E4%B9%90%E5%BA%9C%E6%8C%87%E8%BF%B7.html","title":"乐府指迷","lang":"zh-CN","frontmatter":{"title":"乐府指迷","index":true,"icon":"editor","order":11,"category":["诗藏","词话"],"description":"乐府指迷 [宋]沈义父 论作词之法 余自幼好吟诗。壬寅秋，始识静翁于泽滨。癸卯，识梦窗。暇日相与倡酬，率多填词，因讲论作词之法。然后知词之作难于诗。盖音律欲其协，不协则成长短之诗。下字欲其雅，不雅则近乎缠令之体。用字不可太露，露则直突而无深长之味。发意不可太高，高则狂怪而失柔婉之意。思此，则知所以为难。子侄辈往往求其法于余，姑以得之所闻，条列下方。观于...","head":[["meta",{"property":"og:url","content":"https://ruguoaaa.github.io/xirang/xirang/%E8%AF%97%E8%97%8F/%E8%AF%8D%E8%AF%9D/%E4%B9%90%E5%BA%9C%E6%8C%87%E8%BF%B7.html"}],["meta",{"property":"og:site_name","content":"息壤"}],["meta",{"property":"og:title","content":"乐府指迷"}],["meta",{"property":"og:description","content":"乐府指迷 [宋]沈义父 论作词之法 余自幼好吟诗。壬寅秋，始识静翁于泽滨。癸卯，识梦窗。暇日相与倡酬，率多填词，因讲论作词之法。然后知词之作难于诗。盖音律欲其协，不协则成长短之诗。下字欲其雅，不雅则近乎缠令之体。用字不可太露，露则直突而无深长之味。发意不可太高，高则狂怪而失柔婉之意。思此，则知所以为难。子侄辈往往求其法于余，姑以得之所闻，条列下方。观于..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-05-28T09:40:49.000Z"}],["meta",{"property":"article:author","content":"Paladin"}],["meta",{"property":"article:modified_time","content":"2023-05-28T09:40:49.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"乐府指迷\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2023-05-28T09:40:49.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Paladin\\",\\"url\\":\\"https://ruguoaaa.github.io/xirang\\"}]}"]]},"headers":[{"level":2,"title":"乐府指迷","slug":"乐府指迷","link":"#乐府指迷","children":[]}],"git":{"createdTime":1685266849000,"updatedTime":1685266849000,"contributors":[{"name":"ruguoaaa","email":"ruguoaaa@163.com","commits":1}]},"readingTime":{"minutes":7.76,"words":2328},"filePathRelative":"诗藏/词话/乐府指迷.md","localizedDate":"2023年5月28日","autoDesc":true}');export{t as data};
