import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as a,o as s,c,a as e,b as t,d as n}from"./app-1121ccca.js";const _={},l=e("h2",{id:"介绍",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#介绍","aria-hidden":"true"},"#"),t(" 介绍")],-1),i={href:"https://baike.baidu.com/item/%E4%B8%87%E6%A0%91/3605177",target:"_blank",rel:"noopener noreferrer"},d={href:"https://baike.baidu.com/item/%E8%AF%8D%E5%BE%8B/5405138",target:"_blank",rel:"noopener noreferrer"},h=e("p",null,"但因此书成于万氏飘泊岭南之时，行箧之中，书籍无多，采列各调，录自《花间》、《草堂》、《词林万选》、《词综》及汲古阁刻本，未经细校，故沿诸书而讹误甚多。咸丰间杜文澜吸收前人校正《词律》的成果，作《词律校勘记》二卷，于万氏原书之误叶、误分段落、误题作者姓名、脱漏文字，一一为之厘订，并纠正了分调、分体不尽合理之处。同治末，德清徐本立(诚庵)撰《词律拾遗》八卷，前六卷补《词律》未收之一百六十五调、四百九十五体，后二卷为补注，采杜氏《词律校勘记》居多，也有采自他书补《校勘记》之阙者，订正万氏注之误，清代著名学者俞樾在《词律拾遗序》中称：“今徐君拾遗补阙，绳愆纠缪，又为万氏功臣。”然而此书当时刊而未行。此后，杜文澜又作《词律补遗》一卷，于徐氏《拾遗》之外又得五十调，但杂采宋鼓吹法曲及元人小令，嫌有混词体。近人徐棨又撰《词律笺榷》。徐棨，字戟门，广东番禺人。曾举于乡，早逝。此稿早创未竟，残稿五卷。而比勘精核，纠正万树《词律》谬误甚多。龙榆生刊于《词学季刊》第二卷第二号至第三卷第二号。",-1),p=e("p",null,"有堆絮园原刻本、《四库全书》本、《四部备要》本。清光绪二年(1876)，杜文澜将自撰《词律校勘记》分注于《词律》本调之下，徐本立《词律拾遗》及自撰《词律补遗》附于《词律》之后，合成一册刊行。1984年上海古籍出版社据此影印出版。",-1);function m(f,u){const o=a("ExternalLinkIcon");return s(),c("div",null,[l,e("p",null,[t("词谱。二十卷。清"),e("a",i,[t("万树"),n(o)]),t("撰。万树(1630?—1688)，字红友，又字花农，号山翁。江苏宜兴人。国子监生，羁旅四方，曾在广东、福建作吴兴祚幕宾，卒于广西舟旅中。有才情，善词曲，撰有杂剧、传奇多种。《词律》一书由无锡侯文灿协助，编成于康熙二十六年(1687)。")]),e("p",null,[e("a",d,[t("《词律》"),n(o)]),t("共收唐宋金元词六百六十调，一千一百八十余体，排比以求词律。明清之交，举世奉明代程明善《啸余图谱》为填词准绳，但取便于口吻，不知声律规范，以致“词风愈盛，词学愈衰”(《词律自序》)。万树取唐宋金元诸家名作，“考其调之异同，酌其句之分合，辨其字之平仄，序其篇之短长”(《自序》)，经过对平仄、音韵、句法等详明的校订考证，从而确定了词调各体谱式，纠正了《啸余图谱》和清初赖以邠《填词图谱》分调、分段、分句的很多错误。他反对明刻本《类编草堂诗余》以小令、中调、长调编次，和清毛先舒以字数多寡来划分小令、中调、长调的拘执之见，故列调“以字少居前，字多居后”(《词律发凡》);于句法则详分五言之上二下三、上三下二、上一下四，七言之上四下三、上三下四，四言之有一二一句法;于平仄则谓：“平止一途，仄兼上去入三种，不可遇仄而以三声概填。”“大抵两上两去，在所当避。”“三声之中，上入二者可以作平，去则独异。”(《发凡》)盖申述宋沈义父以入代平、严分去上之说;论句读及拗字，颇为细密，辨元人曲、词之分，斥明人“自度腔”之谬，皆有所据依。康熙时人田同之在《西圃词说》中评价此书：“宋元人所撰词谱流传者少。自国初至康熙十年前，填词家多沿明人，遵守《啸余谱》一书。词句虽胜于前，而音律不协，即《衍波》(王士禛词集名)亦不免矣，此《词律》之所由作也……故浙西名家，务求考订精严，不敢出《词律》范围之外，诚以《词律》为确且善耳。”后来吴衡照《莲子居词话》也说：“万红友当��榛楛之时，为词宗护法，可谓功臣。”乾嘉间音韵学家凌廷堪则称誉“万氏之说与古暗合也”(《梅边吹笛谱·湘月词序》)。《词律》是在《钦定词谱》问世前收体最详、资料最丰、考订最细的一部词谱。")]),h,p])}const E=r(_,[["render",m],["__file","介绍.html.vue"]]);export{E as default};
