import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o,c as s,a as e,b as a}from"./app-e9273545.js";const c={},_=e("h2",{id:"词苑萃编",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#词苑萃编","aria-hidden":"true"},"#"),a(" 词苑萃编")],-1),n=e("p",null,"清 冯金伯辑",-1),r=e("p",null,"自古词章之炳著，不必一出於己也。尼山删诗，昭明选文，夫人知之矣。墨香冯君手一编示西楼，则词苑之萃编也。曰：“是非余之词，而无不可谓余之词也。斐然成章，信诸古人，即信诸今人可也。萃集君言，信诸子大夫，即信之余也。”於是西楼寓客忻然而笑曰：“夫子良是。譬诸上林，楼台之丽也，百卉之鲜也，鱼鸟之乐也，天下之大观，孰有加於上林哉。然而洞庭之乐，钧天之春天，霓裳之舞，亦不可指屈矣。今夫子不倦於编摩，是欲使人间闻天上之曲也。是欲使闾里知上林之华也。将见喜者笑，思者慕，恍见夫子手是编於圣人之居，而纵目於文选之楼也。夫寓客与冯君交三世矣，览泖峰之湖山，采勾曲之丹砂，访王谢之旧宅，觞焉咏焉，历有年矣。乃谓冯君，如怀兹编而秘焉，是不欲今人及古人也，是不信夫仆且不能信诸己也。奚可哉。夫兴观君怨，匪独诗也。诗馀为词，凡幽人迁客，春女秋士，抚今思古，唱予和汝，其致一焉。是必出斯编以示天下後世，则词不必一出於己，而词苑之萃，其信今而传後无疑也。”於是冯君作而言曰：“敬闻命矣。”嘉庆十有一年，岁在丙寅清和，香岩愚弟许兆桂拜序。吴江徐虹亭太史，著有词苑丛谈一书，尤西堂序之，谓其撮前人之标，而搜新剔异，更有闻所未闻者，洵倚声之董狐矣。朱竹尝语太史，捃摭书目，必须备注其下，方不似世儒剿取前人之语，以为己出者。太史亦深韪其言，惜已付梓，无从一一追补。予向读兹书，更惜其序次错综，屡欲重加排篡，匆匆未果。甲子入秋後，枯坐於小舟，萧然无事，思了此愿。第家中书籍，未能捆载而来，此间又无书可借。惟先将原书细为整理，复就案头所有，再为补缀。因陋就简，仍复不免，然比原书删者十之一，增者已十之三四矣。原书分体制、音韵、品藻、纪事、辨证，谐谑、外编七部，予於体制下增旨趣一部，一以溯其渊源，一以穷其阃奥也。於品藻外增指摘一部，一以见欣赏之情，一以寓别裁之意也。至音韵则移於纪事後。外编原载神仙鬼怪之事，但大半已散见於纪事门中，兹惟就各部难於附丽及可附丽而偶尔失载者，改为馀编二卷。手自缮写，逾年而脱稿。订正原书，并无创获。然引书必注，隶事有序，厘然秩然，俾观者快然有当於心，亦庶几为徐氏功臣云尔。嘉庆十年，岁次乙丑中秋後一日，海曲冯金伯识。",-1),d=[_,n,r];function i(l,h){return o(),s("div",null,d)}const u=t(c,[["render",i],["__file","序.html.vue"]]);export{u as default};
