const t=JSON.parse('{"key":"v-3b19b81d","path":"/%E8%AF%97%E8%97%8F/%E8%AF%8D%E8%AF%9D/%E8%AF%8D%E5%9D%9B%E4%B8%9B%E8%AF%9D.html","title":"","lang":"zh-CN","frontmatter":{"description":"词坛丛话 清 陈廷焯撰 ○词肇于赓歌 唐以前无词名，然词之源，肇于赓歌，成于乐府。汉郊祀歌、短箫饶歌诸篇，长短句不一，是词之祖也。迄于六代，江南采莲诸曲，去倚声不远。其不即变为词者，律体未兴，古风犹未远也。自古诗变为近体，五七言各分古、律、绝，传于伶官乐部。长短句无所依，而词于是作焉。兹将汉晋六朝诸歌曲，择其类于词者若干首，录入杂体一卷，亦数典不忘祖之...","head":[["meta",{"property":"og:url","content":"https://ruguoaaa.github.io/xirang/xirang/%E8%AF%97%E8%97%8F/%E8%AF%8D%E8%AF%9D/%E8%AF%8D%E5%9D%9B%E4%B8%9B%E8%AF%9D.html"}],["meta",{"property":"og:site_name","content":"息壤"}],["meta",{"property":"og:description","content":"词坛丛话 清 陈廷焯撰 ○词肇于赓歌 唐以前无词名，然词之源，肇于赓歌，成于乐府。汉郊祀歌、短箫饶歌诸篇，长短句不一，是词之祖也。迄于六代，江南采莲诸曲，去倚声不远。其不即变为词者，律体未兴，古风犹未远也。自古诗变为近体，五七言各分古、律、绝，传于伶官乐部。长短句无所依，而词于是作焉。兹将汉晋六朝诸歌曲，择其类于词者若干首，录入杂体一卷，亦数典不忘祖之..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-05-28T09:40:49.000Z"}],["meta",{"property":"article:author","content":"Paladin"}],["meta",{"property":"article:modified_time","content":"2023-05-28T09:40:49.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2023-05-28T09:40:49.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Paladin\\",\\"url\\":\\"https://ruguoaaa.github.io/xirang\\"}]}"]]},"headers":[],"git":{"createdTime":1685266849000,"updatedTime":1685266849000,"contributors":[{"name":"ruguoaaa","email":"ruguoaaa@163.com","commits":1}]},"readingTime":{"minutes":24.11,"words":7234},"filePathRelative":"诗藏/词话/词坛丛话.md","localizedDate":"2023年5月28日","autoDesc":true,"excerpt":""}');export{t as data};
